import {CREATE_POST} from "./types";

export function createPost(post) {
    return {
        type: CREATE_POST,
        payload: post
    }
}

export function fetchPosts() {
    return async dispatch => {
        const response = await fetch('');
        const json = await response.json();
        dispatch()
    }
}
